import React, { useState } from 'react';
import Table from 'react-bootstrap/Table';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import ProductPickerDialog from '../components/productpicker';

const ProductManagement = () => {
    const [showDialog, setShowDialog] = useState(false);
    const [selectedProducts, setSelectedProducts] = useState([]);
    const [editingIndex, setEditingIndex] = useState(null);

    // Open dialog for adding or editing a product
    const handleOpenDialog = (index = null) => {
        setEditingIndex(index);
        setShowDialog(true);
    };

    // Close the product picker dialog
    const handleCloseDialog = () => {
        setShowDialog(false);
        setEditingIndex(null);
    };

    // Handle product selection from the dialog
    const handleProductSelect = (products, variants) => {
        const selectedProduct = products[0]; // Assuming only one product is selected at a time
        const productWithVariants = {
            ...selectedProduct,
            variants: variants.filter(variant => variant.product_id === selectedProduct.id),
        };

        if (editingIndex !== null) {
            // Update the product at the editing index
            setSelectedProducts((prev) =>
                prev.map((product, index) =>
                    index === editingIndex ? productWithVariants : product
                )
            );
        } else {
            // Add a new product
            setSelectedProducts((prev) => [...prev, productWithVariants]);
        }

        setShowDialog(false);
        setEditingIndex(null);
    };

    const handleAddProduct = () => {
        // Add a placeholder for a new product
        setSelectedProducts((prev) => [...prev, { id: null, title: '', variants: [] }]);
    };

    return (
        <Container>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Discount</th>
                    </tr>
                </thead>
                <tbody>
                    {/* Initial Input Box */}
                    <tr>
                        <td>
                            <Row className="align-items-center">
                                <Col xs="auto" className="d-flex justify-content-center">
                                    {/* Placeholder for drag handle */}
                                </Col>
                                <Col className="d-flex justify-content-center">
                                    <InputGroup className="mb-3">
                                        <Form.Control
                                            className="form-control-lg"
                                            placeholder="Select Product"
                                            aria-label="Select Product"
                                            onClick={() => handleOpenDialog(0)} // Open dialog for editing the first product
                                        />
                                    </InputGroup>
                                </Col>
                            </Row>
                        </td>
                        <td>
                            <Button variant="primary">Add Discount</Button>
                        </td>
                    </tr>

                    {/* Render dynamically added products */}
                    {selectedProducts.map((product, index) => (
                        <tr key={index}>
                            <td>
                                <InputGroup className="mb-3">
                                    <Form.Control
                                        className="form-control-lg"
                                        value={product.title || 'Select Product'}
                                        readOnly
                                        placeholder="Select Product"
                                    />
                                    <Button
                                        variant="outline-secondary"
                                        onClick={() => handleOpenDialog(index + 1)} // Open dialog for editing
                                    >
                                        Edit
                                    </Button>
                                </InputGroup>

                                {/* Variants */}
                                {product.variants.map((variant) => (
                                    <Row key={variant.id} className="mb-2">
                                        <Col xs="auto" className="d-flex align-items-center">
                                            {/* Optional drag handle */}
                                        </Col>
                                        <Col>
                                            <Form.Control value={variant.title} readOnly />
                                        </Col>
                                    </Row>
                                ))}
                            </td>
                            <td>
                                <Button variant="primary">Add Discount</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>

            {/* Add Product Button */}
            <Button variant="success" onClick={handleAddProduct}>
                Add Product
            </Button>

            <ProductPickerDialog
                show={showDialog}
                onClose={handleCloseDialog}
                onProductSelect={handleProductSelect}
            />
        </Container>
    );
};

export default ProductManagement;
