import React, { useState } from 'react';
import Table from 'react-bootstrap/Table';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Image from 'react-bootstrap/Image';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import ProductPickerDialog from '../components/productpicker';


const ProductManagement = () => {
    const [showDialog,setShowDialog]=useState(false)
    const [selectedProducts, setSelectedProducts] = useState([]);
    const handleCloseDialog = () => {
        console.log("clode")
        setShowDialog(false); // This will close the dialog
    };

    // Handler function to open the dialog
    const handleOpenDialog = () => {
        console.log("open")
        setShowDialog(true); // This will open the dialog
    };
    const handleProductSelect = (products) => {
        setSelectedProducts(products);
    };
    return (
        <Container>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Discount</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>
                            <Row className="align-items-center">
                                <Col xs="auto" className="d-flex justify-content-center">
                                    <Image src="/images/dots.png" fluid />
                                </Col>
                                <Col className="d-flex justify-content-center">
                                    <InputGroup className="mb-3">
                                        <Form.Control
                                            className="form-control-lg" // Make input field bigger
                                            placeholder="Search product"
                                            aria-label="Search product"
                                            aria-describedby="basic-addon1"
                                            onFocus={() => setShowDialog(true)}
                                        />
                                    </InputGroup>
                                </Col>
                            </Row>
                        </td>
                        <td>
                            <Button variant="primary">Add discount</Button>
                        </td>
                    </tr>
                </tbody>
            </Table>
           {showDialog && <ProductPickerDialog
                show={showDialog}         // Control the visibility of the dialog
                onClose={handleCloseDialog} // Close the dialog when needed
                onProductSelect={handleProductSelect} // Handle selected products
            />} 
        </Container>
    );
};

export default ProductManagement;
