// src/components/ProductItem.js

import React from 'react';
import {
  Flex,
  IconButton,
  Input,
  Td,
  Box,
  Button,
} from '@chakra-ui/react';
import { EditIcon, CloseIcon } from '@chakra-ui/icons';
import ProductVariant from './productvariant';

const ProductItem = ({ product, onRemove, onApplyVariantDiscount, onRemoveVariant }) => {
  return (
    <>
      <Td>
        <Flex align="center">
          <Input value={product.title} readOnly placeholder="Select Product" mr="2" />
          <IconButton icon={<EditIcon />} size="sm" />
        </Flex>
      </Td>
      <Td>
        <Button size="sm">Add Discount</Button>
      </Td>
      <Td>
        <IconButton icon={<CloseIcon />} size="sm" onClick={onRemove} />
      </Td>

      {/* Show variants if available */}
      <Td colSpan={3}>
        {product.variants?.map((variant) => (
          <ProductVariant
            key={variant.id}
            variant={variant}
            onApplyDiscount={(variantId, discount) => onApplyVariantDiscount(product.id, variantId, discount)}
            onRemoveVariant={(variantId) => onRemoveVariant(product.id, variantId)}
          />
        ))}
      </Td>
    </>
  );
};

export default ProductItem;
