import React, { createContext, useContext, useState } from 'react';

// Create the Product Context
const ProductContext = createContext();

// Custom hook to use Product Context
export const useProductContext = () => useContext(ProductContext);

export const ProductProvider = ({ children }) => {
  const [products, setProducts] = useState([]);

  const addProduct = () => {
    setProducts((prev) => [
      ...prev,
      { id: Date.now(), name: 'New Product', variants: [] },
    ]);
  };

  const removeProduct = (id) => {
    setProducts((prev) => prev.filter((product) => product.id !== id));
  };

  const reorderProducts = (dragIndex, hoverIndex) => {
    const reordered = [...products];
    [reordered[dragIndex], reordered[hoverIndex]] = [reordered[hoverIndex], reordered[dragIndex]];
    setProducts(reordered);
  };

  const applyDiscount = (id, type) => {
    setProducts((prev) =>
      prev.map((product) =>
        product.id === id ? { ...product, discount: type } : product
      )
    );
  };

  return (
    <ProductContext.Provider
      value={{
        products,
        addProduct,
        removeProduct,
        reorderProducts,
        applyDiscount,
      }}
    >
      {children}
    </ProductContext.Provider>
  );
};
