// src/components/ProductPickerDialog.js
import React, { useState, useEffect } from 'react';
import Modal from 'react-bootstrap/Modal';
import Table from 'react-bootstrap/Table';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Image from 'react-bootstrap/Image';
import axios from 'axios';

const ProductPickerDialog = ({ show, onClose, onProductSelect }) => {
    const [searchTerm, setSearchTerm] = useState('');
    const [products, setProducts] = useState([]);
    const [selectedProducts, setSelectedProducts] = useState([]);

    useEffect(() => {
        if (searchTerm) {
            fetchProducts(searchTerm);
        }
    }, [searchTerm]);

    const fetchProducts = async (search) => {
        try {
            const response = await axios.get(
                `http://stageapi.monkcommerce.app/task/products/search`,
                {
                    params: { search, page: 1, limit: 10 },
                    headers: {
                        'x-api-key': '72njgfa948d9aS7gs5'  // Replace with your actual API key
                    }
                }            );
            setProducts(response.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const handleSelectProduct = (product) => {
        const isSelected = selectedProducts.includes(product);
        if (isSelected) {
            setSelectedProducts(selectedProducts.filter(p => p.id !== product.id));
        } else {
            setSelectedProducts([...selectedProducts, product]);
        }
    };

    const handleAddProducts = () => {
        onProductSelect(selectedProducts);
        onClose();
    };

    return (
        <Modal show={show} onHide={onClose} size="lg" centered>
            <Modal.Header closeButton>
                <Modal.Title>Select Products</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form.Control
                    type="text"
                    placeholder="Search product"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="mb-3"
                />
                <Table bordered>
                    <thead>
                        <tr>
                            <th></th>
                            <th>Product</th>
                            <th>Variants</th>
                            <th>Price</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map((product) => (
                            <React.Fragment key={product.id}>
                                <tr>
                                    <td>
                                        <Form.Check
                                            type="checkbox"
                                            checked={selectedProducts.includes(product)}
                                            onChange={() => handleSelectProduct(product)}
                                        />
                                    </td>
                                    <td>
                                        <Image src={product.image.src} thumbnail width={50} />{' '}
                                        {product.title}
                                    </td>
                                    <td colSpan="2">
                                        {product.variants.map((variant) => (
                                            <div key={variant.id}>
                                                {variant.title} - ${variant.price}
                                            </div>
                                        ))}
                                    </td>
                                </tr>
                            </React.Fragment>
                        ))}
                    </tbody>
                </Table>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={onClose}>
                    Cancel
                </Button>
                <Button variant="primary" onClick={handleAddProducts}>
                    Add
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default ProductPickerDialog;
