import React from "react";
import { IoIosAddCircle } from "react-icons/io";
import { IoIosCloseCircle } from "react-icons/io";

import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

import {
    Box,
    Table,
  
    Tbody,
    Td,
    Th,
    Thead,
    Tr,
    Button,
    IconButton

} from "@chakra-ui/react";

{/* <IoIosAddCircle /> */ }
{/* <IoIosCloseCircle /> */ }

const productList = ({ products, onAddProduct, onRemoveProduct, onReorder }) => {
    const handleDragEnd = (result) => {
        if (!result.destination) return;
        const reorderedProducts = Array.from(products);
        const [movedProduct] = reorderedProducts.splice(result.source.index, 1);
        reorderedProducts.splice(result.destination.index, 0, movedProduct);
        onReorder(reorderedProducts);
      };
    return (
        <Box>
            <Table varient="simple">
                <Thead>
                    <Tr>
                        <Th>Product</Th>
                        <Th>Discount</Th>
                    </Tr>
                </Thead>
                <DragDropContext onDragEnd={handleDragEnd}>
                    <Droppable droppableId="product-list">
                        {(provided) => (
                            <Tbody {...provided.droppableProps} ref={provided.innerRef}>
                                {products.map((product, index) => (
                                    <Draggable key={product.id} draggableId={product.id.toString()} index={index}>
                                        {(provided) => (
                                            <Tr ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                                                <ProductItem product={product} onRemove={() => onRemoveProduct(product.id)} />
                                            </Tr>
                                        )}
                                    </Draggable>
                                ))}
                                {provided.placeholder}
                            </Tbody>
                        )}
                    </Droppable>
                </DragDropContext>



            </Table>
            <Button leftIcon={<AddIcon />} mt="4" onClick={onAddProduct}>
                Add Product
            </Button>
        </Box>
    )
}


export default productList
