import { Box, Text, Button, IconButton } from '@chakra-ui/react';
import { useState } from 'react';
import { FaTimes } from 'react-icons/fa';
import { useProductContext } from '../context/ProductContext';

const ProductItem = ({ product }) => {
  const { removeProduct, applyDiscount } = useProductContext();
  const [showDiscountOptions, setShowDiscountOptions] = useState(false);

  return (
    <Box border="1px solid #ddd" p="4" mb="2" borderRadius="md">
      <Text fontSize="lg">{product.name}</Text>
      <Button onClick={() => setShowDiscountOptions(!showDiscountOptions)}>
        {showDiscountOptions ? 'Hide Discount Options' : 'Add Discount'}
      </Button>
      {showDiscountOptions && (
        <Box mt="2">
          <Button onClick={() => applyDiscount(product.id, 'flat')}>Flat Discount</Button>
          <Button onClick={() => applyDiscount(product.id, 'percent')}>Percentage Discount</Button>
        </Box>
      )}
      <IconButton icon={<FaTimes />} onClick={() => removeProduct(product.id)} aria-label="Remove Product" />
    </Box>
  );
};

export default ProductItem;
