import { useState } from "react";

const useProductList = () => {
    const [products, setProducts] = useState([]);

    const fetchProducts = async () => {
        try {
            // Uncomment and adjust the following if you have an actual API endpoint:
            // const response = await fetch('/api/products');
            // const data = await response.json();

            // Mock data for local testing
            const data = [
                {
                    "id": 77,
                    "title": "Fog Linen Chambray Towel - Beige Stripe",
                    "variants": [
                        { "id": 1, "product_id": 77, "title": "XS / Silver", "price": "49" },
                        { "id": 2, "product_id": 77, "title": "S / Silver", "price": "49" },
                        { "id": 3, "product_id": 77, "title": "M / Silver", "price": "49" }
                    ],
                    "image": {
                        "id": 266,
                        "product_id": 77,
                        "src": "https://cdn11.bigcommerce.com/s-p1xcugzp89/products/77/images/266/foglinenbeigestripetowel1b.1647248662.386.513.jpg?c=1"
                    }
                },
                {
                    "id": 80,
                    "title": "Orbit Terrarium - Large",
                    "variants": [
                        { "id": 64, "product_id": 80, "title": "Default Title", "price": "109" }
                    ],
                    "image": {
                        "id": 272,
                        "product_id": 80,
                        "src": "https://cdn11.bigcommerce.com/s-p1xcugzp89/products/80/images/272/roundterrariumlarge.1647248662.386.513.jpg?c=1"
                    }
                }
            ];

            setProducts(data);

        } catch (error) {
            console.error("Error fetching data:", error);
        }
    };

    const addProduct = () => {
        setProducts([...products, { id: Date.now(), title: "", variants: [] }]);
    };

    const removeProduct = (id) => {
        setProducts(products.filter((product) => product.id !== id));
    };

    const reorderProducts = (newOrder) => {
        setProducts(newOrder);
    };

    return {
        products,
        setProducts,
        fetchProducts,
        addProduct,
        removeProduct,
        reorderProducts,
    };
};

export default useProductList;
