import React, { useState } from 'react';
import Table from 'react-bootstrap/Table';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import ProductPickerDialog from '../components/productpicker';

const ProductManagement = () => {
    const [showDialog, setShowDialog] = useState(false);
    const [selectedProducts, setSelectedProducts] = useState([]);

    const handleCloseDialog = () => {
        setShowDialog(false); // Close the dialog
    };

    const handleOpenDialog = () => {
        setShowDialog(true); // Open the dialog
    };

    // Handle selected products and variants from the dialog
    const handleProductSelect = (products, variants) => {
        const updatedProducts = products.map(product => ({
            ...product,
            variants: variants.filter(variant => variant.product_id === product.id),
        }));
        setSelectedProducts(updatedProducts);
        setShowDialog(false); // Close the dialog after selection
    };

    const handleEditProduct = (index) => {
        setShowDialog(true); // Open the dialog for editing
        // Optionally, pass the current product details to the dialog for editing
    };

    return (
        <Container>
            <h4>Add Bundle Products (Max. 4 Products)</h4>
            <p>
                <i>
                    Offer Bundle will be shown to the customer whenever any of the bundle products are added to the cart.
                </i>
            </p>

            {/* Render the list of selected products */}
            {selectedProducts.map((product, index) => (
                <div key={product.id}>
                    {/* Product Title with Edit Button */}
                    <InputGroup className="mb-3">
                        <Form.Control
                            className="form-control-lg"
                            value={product.title}
                            placeholder="Select Product"
                            readOnly
                        />
                        <Button variant="outline-secondary" onClick={() => handleEditProduct(index)}>
                            Edit
                        </Button>
                    </InputGroup>

                    {/* Variants List */}
                    {product.variants.map(variant => (
                        <Row key={variant.id} className="align-items-center mb-2">
                            <Col xs="auto" className="d-flex justify-content-center">
                                {/* Drag handle or placeholder */}
                            </Col>
                            <Col>
                                <Form.Control
                                    className="form-control"
                                    value={variant.title}
                                    readOnly
                                />
                            </Col>
                            <Col xs="auto">
                                <Button
                                    variant="danger"
                                    size="sm"
                                    onClick={() =>
                                        setSelectedProducts(prevProducts =>
                                            prevProducts.map((p, i) =>
                                                i === index
                                                    ? {
                                                          ...p,
                                                          variants: p.variants.filter(v => v.id !== variant.id),
                                                      }
                                                    : p
                                            )
                                        )
                                    }
                                >
                                    Remove
                                </Button>
                            </Col>
                        </Row>
                    ))}
                </div>
            ))}

            {/* Add Product Button */}
            <Button
                variant="success"
                onClick={handleOpenDialog}
                disabled={selectedProducts.length >= 4}
            >
                Add Product
            </Button>

            {/* Product Picker Dialog */}
            <ProductPickerDialog
                show={showDialog}
                onClose={handleCloseDialog}
                onProductSelect={handleProductSelect}
            />
        </Container>
    );
};

export default ProductManagement;
